    this.prepareTreeData(res.data)
  }

  onBackPress() {
    if (this.showPlayer) {
      this.closePlayer()
      return true
    }
    return false
  }

  get displayList() {
    const list: any[] = []
    const traverse = (nodes: any[]) => {
      nodes.forEach((node) => {
        if (!this.isDeviceNode(node)) {
          list.push(node)
          if (node.hasChildren && node.expanded) {
            traverse(node.children)
          }
        }
      })
    }
    traverse(this.nestedTree)
    return list
  }

  private prepareTreeData(data: any) {
    this.nestedTree = this.transformToNestedTree(data, 0)
    if (this.nestedTree.length > 0) {
      const deepestNode = this.expandDeepest(this.nestedTree[0])
      this.handleNodeClick(deepestNode)
    }
  }

  private isDeviceNode(node: any): boolean {
    return node?.playInfo != null
  }

  private expandDeepest(node: any): any {
    if (this.isDeviceNode(node))
      return null
    node.expanded = true

    const nonDeviceChildren = (node.children || []).filter(
      (child: any) => !this.isDeviceNode(child),
    )

    if (nonDeviceChildren.length > 0) {
      return this.expandDeepest(nonDeviceChildren[0])
    }
    return node
  }

  private transformToNestedTree(nodes: any[], level: number): any[] {
    return nodes.map((node) => {
      const children = node.children || []
      const hasChildren = Array.isArray(children) && children.length > 0
      return {
        ...node,
        level,
        hasChildren,
        expanded: false,
        imageLoaded: true,
        children: hasChildren ? this.transformToNestedTree(children, level + 1) : [],
      }
    })
  }

  private collectPlayableDevices(node: any): any[] {
    const devices: any[] = []
    const traverse = (currentNode: any) => {
      if (!currentNode.children)
        return
      currentNode.children.forEach((child: any) => {
        if (this.isDeviceNode(child)) {
          devices.push(child)
        }
        else if (child.children) {
          traverse(child)
        }
      })
    }
    traverse(node)
    return devices
  }

  private toggleExpand(node: any) {
    if (!this.isDeviceNode(node) && node.hasChildren) {
      node.expanded = !node.expanded
      this.$set(node, 'expanded', node.expanded)
    }
  }

  private handleNodeClick(node: any) {
    if (this.isDeviceNode(node))
      return
    if (node.hasChildren) {
      this.toggleExpand(node)
    }
    else {
      this.showTree = false
    }

    this.selectedId = node.id
    this.selectedLabel = node.labelNotes
    this.videoList = this.collectPlayableDevices(node)
  }

  private playVideo(item: any) {
    if (!item.playInfo?.url) {
      this.$u.toast('该视频暂时无法播放')
      return
    }
    this.playTitle = item.labelNotes
    this.fullVideoUrl = this.baseUrl + item.playInfo.url
    this.showPlayer = true
    this.showError = false
    this.reloadKey++
  }

  private closePlayer() {
    this.showPlayer = false
    this.showError = false
  }

  private retryPlay() {
    this.showError = false
    this.reloadKey++
  }

  private handlePlayerError() {
    this.showError = true
  }
}
</script>

