          <scroll-view scroll-y class="tree-scroll-view">
            <view
              v-for="node in displayList" :key="node.id" :style="{ '--level': node.level }" class="tree-node"
              @click="handleNodeClick(node)"
            >
              <view class="node-content" :class="{ selected: selectedId === node.id }">
                <u-icon
                  v-if="selectedId === node.id" name="checkbox-mark" size="20" class="select-icon"
                  color="#409eff"
                />
                <u-icon
                  v-else :name="node.expanded ? 'minus' : 'plus'" size="20" class="expand-icon"
                  @click.stop="toggleExpand(node)"
                />
                <text class="node-label">
                  {{ node.labelNotes }}
                </text>
              </view>
            </view>
          </scroll-view>
        </view>
      </u-popup>
    </view>

    <!-- 视频列表模块 -->
    <view class="video-container">
      <view v-for="(item, i) in videoList" :key="i" class="video-item" @click="playVideo(item)">
        <view class="video-title">
          <view class="status-dot" :class="[item.status ? 'online' : 'offline']" />
          <text>{{ item.labelNotes }}</text>
        </view>
        <view class="thumb-container">
          <image
            class="video-thumb" :src="item.imageLoaded ? `${baseUrl}${item.playInfo.snap}` : ''"
            :class="{ placeholder: !item.imageLoaded }" @error="() => { item.imageLoaded = false; }"
          />
        </view>
      </view>
    </view>

    <!-- 视频播放模块 -->
    <view v-if="showPlayer" class="player-fullscreen">
      <!-- H5端使用自定义顶部栏 -->
      <!-- #ifdef H5 -->
      <view class="player-header">
        <u-icon name="arrow-left" size="32" color="#000" class="back-icon" @click="closePlayer" />
        <text class="player-title">
          {{ playTitle }}
        </text>
        <u-icon name="reload" size="32" color="#000" class="reload-icon" @click="retryPlay" />
      </view>
      <!-- #endif -->

      <view class="player-content">
        <!-- H5平台使用iframe -->
        <!-- #ifdef H5 -->
        <iframe
          v-if="!showError" :key="reloadKey" :src="fullVideoUrl" class="video-frame"
          allow="autoplay; fullscreen; screen-wake-lock" allowfullscreen @error="handlePlayerError"
        />
        <!-- #endif -->

        <!-- APP和小程序平台使用web-view -->
        <!-- #ifdef APP-PLUS || MP -->
        <web-view v-if="!showError" :key="reloadKey" :src="fullVideoUrl" class="video-frame" @error="handlePlayerError" />
        <!-- #endif -->

        <!-- 错误提示 -->
        <view v-if="showError" class="error-message">
          <text>视频加载失败</text>
          <button class="reload-btn" @click="retryPlay">
            重新加载
          </button>
        </view>
      </view>
    </view>
  </view>
</template>

